/* 1. Widok dla planety */ 
CREATE OR REPLACE VIEW  "Activity" AS SELECT "useractivity_useractivity"."id", "useractivity_useractivity"."activity_type", "useractivity_useractivity"."user_id", "useractivity_useractivity"."object_id", "useractivity_useractivity"."post_id", "useractivity_useractivity"."short_review_id", "useractivity_useractivity"."comment_id", "useractivity_useractivity"."link_id", "useractivity_useractivity"."film_id", "useractivity_useractivity"."person_id", "useractivity_useractivity"."rate_type", "useractivity_useractivity"."rated_objects_count", "useractivity_useractivity"."rated_objects_html", "useractivity_useractivity"."modified_at", "useractivity_useractivity"."created_at", "useractivity_useractivity"."LANG" FROM "useractivity_useractivity" LEFT JOIN "blog_post" ON ("useractivity_useractivity"."post_id"="blog_post"."parent_id" and "blog_post"."post_status"=2) LEFT JOIN "externallink_externallink" ON ("useractivity_useractivity"."link_id" = "externallink_externallink"."id" and "externallink_externallink"."is_deleted" = false) ORDER BY "useractivity_useractivity"."created_at" DESC;